    @inlinable func rcResetStyles() {
        gtk_rc_reset_styles(settings_ptr)
    
    }

    @inlinable var parentInstance: GObject {
        get {
            let rv = settings_ptr.pointee.parent_instance
            return rv
        }
    }

    // var priv is unavailable because priv is private

